        "parameters": [
          {
            "id": "88bda73f-158a-4971-9310-1e557a8f89f0",
            "version": "KqlParameterItem/1.0",
            "name": "TimeRange",
            "label": "Time Range",
            "type": 4,
            "isRequired": true,
            "value": {
              "durationMs": 2592000000
            },
            "typeSettings": {
              "selectableValues": [
                {
                  "durationMs": 900000
                },
                {
                  "durationMs": 3600000
                },
                {
                  "durationMs": 14400000
                },
                {
                  "durationMs": 43200000
                },
                {
                  "durationMs": 86400000
                },
                {
                  "durationMs": 259200000
                },
                {
                  "durationMs": 604800000
                },
                {
                  "durationMs": 2592000000
                },
                {
                  "durationMs": 5184000000
                },
                {
                  "durationMs": 7776000000
                }
              ],
              "allowCustom": true
            }
          },
          {
            "id": "6e7ddca7-163c-4547-9e37-d408299ce828",
            "version": "KqlParameterItem/1.0",
            "name": "HostPool",
            "label": "Host Pool",
            "type": 2,
            "description": "Select a host pool to show only data of this specific one",
            "isRequired": true,
            "query": "WVDConnections | extend HostPool=split(_ResourceId,\"/\")[8] | where TimeGenerated {TimeRange} | distinct tostring(HostPool) | order by HostPool",
            "value": "design",
            "typeSettings": {
              "additionalResourceOptions": []
            },
            "timeContext": {
              "durationMs": 0
            },
            "timeContextFromParameter": "TimeRange",
            "queryType": 0,
            "resourceType": "microsoft.operationalinsights/workspaces"
          },
          {
            "id": "682005d8-aaaf-495d-95b7-3b8dd7bb731c",
            "version": "KqlParameterItem/1.0",
            "name": "SessionHostSelector",
            "label": "Opt: Session Host",
            "type": 2,
            "description": "Select a session host to drill down into the data. Don't select a session host to disable this filter.",
            "multiSelect": true,
            "quote": "'",
            "delimiter": ",",
            "query": "WVDConnections | extend HostPool=split(_ResourceId,\"/\")[8] | where TimeGenerated {TimeRange} and HostPool==\"{HostPool}\" |distinct SessionHostName | order by SessionHostName",
            "value": [],
            "typeSettings": {
              "additionalResourceOptions": [],
              "showDefault": false
            },
            "timeContext": {
              "durationMs": 0
            },
            "timeContextFromParameter": "TimeRange",
            "queryType": 0,
            "resourceType": "microsoft.operationalinsights/workspaces"
          },
          {
            "id": "fc314454-4e21-4e3e-8fdb-94f2088d70f9",
            "version": "KqlParameterItem/1.0",
            "name": "UserSelector",
            "label": "Opt: User",
            "type": 2,
            "description": "Select a user to drill down into the data. Don't select a user to disable this filter.",
            "multiSelect": true,
            "quote": "'",
            "delimiter": ",",
            "query": "WVDConnections | extend HostPool=split(_ResourceId,\"/\")[8] | where TimeGenerated {TimeRange} and HostPool==\"{HostPool}\" | distinct UserName | order by UserName",
            "typeSettings": {
              "additionalResourceOptions": [],
              "showDefault": false
            },
            "timeContext": {
              "durationMs": 0
            },
            "timeContextFromParameter": "TimeRange",
            "queryType": 0,
            "resourceType": "microsoft.operationalinsights/workspaces"
          },
          {
            "id": "25c6a654-a08a-4020-99ba-0563a66fd201",
            "version": "KqlParameterItem/1.0",
            "name": "ErrorSelector",
            "label": "Error Selector",
            "type": 2,
            "description": "Select an error to drill down into the data. Don't select an item to disable this filter.",
            "multiSelect": true,
            "quote": "'",
            "delimiter": ",",
            "query": "WVDErrors | extend HostPool=split(_ResourceId,\"/\")[8] | where TimeGenerated {TimeRange} and HostPool==\"{HostPool}\" \r\n| extend CodeSymbolic=iff(CodeSymbolic startswith_cs \"Unspecified error\",\"Unspecified error\",CodeSymbolic)\r\n| extend CodeSymbolic=iff(CodeSymbolic startswith_cs \"The system cannot open the file.\",\"The system cannot open the file.\",CodeSymbolic)\r\n| extend CodeSymbolic=iff(CodeSymbolic startswith_cs \"The RPC server is unavailable.\",\"The RPC server is unavailable.\",CodeSymbolic)\r\n| extend CodeSymbolic=iff(CodeSymbolic startswith_cs \"No such interface supported\",\"No such interface supported\",CodeSymbolic)\r\n| extend CodeSymbolic=iff(CodeSymbolic startswith_cs \"No process is on the other end of the pipe.\",\"No process is on the other end of the pipe.\",CodeSymbolic)\r\n| extend CodeSymbolic=iff(CodeSymbolic startswith_cs \"Operation aborted\",\"\tOperation aborted\",CodeSymbolic)\r\n| distinct tostring(CodeSymbolic) | order by CodeSymbolic",
            "typeSettings": {
              "additionalResourceOptions": [],
              "showDefault": false
            },
            "timeContext": {
              "durationMs": 0
            },
            "timeContextFromParameter": "TimeRange",
            "queryType": 0,
            "resourceType": "microsoft.operationalinsights/workspaces"
          }
        ],
        "style": "pills",
        "doNotRunWhenHidden": true,
        "queryType": 0,
        "resourceType": "microsoft.operationalinsights/workspaces"
      },
      "name": "parameters - 2"
    },
    {
      "type": 3,
      "content": {
        "version": "KqlItem/1.0",
        "query": "WVDConnectionNetworkData | extend HostPool=split(_ResourceId,\"/\")[8] | where TimeGenerated {TimeRange} and HostPool==\"{HostPool}\"\r\n| join kind=inner  (WVDConnections| where TimeGenerated {TimeRange} and State ==\"Connected\" and UserName !=\"\" and (\"{UserSelector}\"==\"\" or UserName in ('' {UserSelector})) and (column_ifexists(\"SessionHostName\",\"*\")==\"*\" or \"{SessionHostSelector}\"==\"\" or column_ifexists(\"SessionHostName\",\"\") in ('' {SessionHostSelector})))  on CorrelationId\r\n| summarize [\"Avg. RTT\"]=avg(EstRoundTripTimeInMs),[\"Max. RTT\"]=max(EstRoundTripTimeInMs),[\"P90 RTT\"]=percentile(EstRoundTripTimeInMs,90),[\"Avg. Bandwidth\"]=avg(EstAvailableBandwidthKBps),[\"Max. Bandwidth\"]=max(EstAvailableBandwidthKBps),[\"P90 Bandwidth\"]=percentile(EstAvailableBandwidthKBps,90) by UserName\r\n| order by [\"Avg. RTT\"] desc\r\n",
        "size": 0,
        "title": "RTT and Bandwidth by User",
        "timeContext": {
          "durationMs": 0
        },
        "timeContextFromParameter": "TimeRange",
        "exportFieldName": "UserName",
        "exportParameterName": "Select_SourceNetwork_A_UserName",
        "showExportToExcel": true,
        "exportToExcelOptions": "all",
